import logo from './logo.svg';
import './App.css';
import {Planets} from "./Planets";
import {useState} from "react";

function App() {

    const [content, setContent] = useState([]);
    const [screen, setScreen] = useState("2")
    //call this to keep displaying on screen.

    const updateContent = (number) => {
        content.push(number)
        console.log(content)
        //return 0;
    }
    // call this when = is pressed.
    const evaluate = () => {

    }
    const updateScreen = (number) => {
        updateContent(number)
        let temp = ""
        for (let i = 0; i < content.length; i++){
            temp += content[i]
        }
        setScreen(screen + temp)
    }

    return (
        <div className="App">Hello World
            <div id="everything">
                <div id="calculator">
                    <div id="screen">{screen}</div>
                        <div id="operations">
                            <button>/</button>
                            <button>x</button>
                            <button>-</button>
                            <button>+</button>
                            <button>DEL</button>
                        </div>
                <div id="numbers">
                    <button value={1} onClick={ () => {updateScreen(1)}}>1</button>
                    <button value={2}>2</button>
                    <button>3</button>
                    <button>4</button>
                    <button>5</button>
                    <button>6</button>
                    <button>7</button>
                    <button>8</button>
                    <button>9</button>
                    <button>0</button>
                    <button>.</button>
                    <button>=</button>
                </div>
            </div>
        </div>
    </div>
    );

}
export default App;
