import logo from './logo.svg';
import './App.css';
import {Planets} from "./Planets";
import {useState} from "react";

function App() {

    const [screen, setScreen] = useState("0")
    const [content, setContent] = useState([])

    const createDigits = () => {
        const digits = [];
        for (let i = 1; i < 10; i++){
            digits.push(<button key={i} onClick={ () => updateScreen(i)}>{i}</button> )
        }
        return digits
    }
    const clearArray = () => {
        setContent([])
    }

    //updates display of screen
    const updateScreen = (number) => {
        // populates array
        // if the button is pressed after "Error" displays
        if (screen === "Error" || screen === "0"){
            // empty array
            clearArray()
            setScreen(String(number))
        }

        else {
            content.push(number)
            console.log(content)
            setScreen(screen + String(content[content.length - 1]) )
        }
    }

    // equals
    const evaluate = () => {
        if (screen === "Error"){
            setScreen("0")
        }

        else {
            try {
                setScreen(String(eval(screen)))
            } catch (err) {
                console.log(err.message)
                setScreen("Error")
            }
        }
        // if eval throws an exception, display error and empty array.
    }


    const deleteContent = () => {
        console.log(screen)
        if (screen === "Error" || screen.length === 1){
            clearArray()
            setScreen("0")
        }

        else{
            content.pop()
            setScreen(screen.slice(0, screen.length - 1))
            console.log(content)
         }
    }

    return (
        <div className="App">Hello World
            <div id="everything">
                <div id="calculator">
                    <div id="screen">{screen}</div>
                        <div id="operations">
                            <button onClick={ () => {updateScreen("/")}}>/</button>
                            <button onClick={ () => {updateScreen("*")}}>x</button>
                            <button onClick={ () => {updateScreen("-")}}>-</button>
                            <button onClick={ () => {updateScreen("+")}}>+</button>
                            <button onClick={deleteContent}>DEL</button>
                        </div>
                <div id="numbers">
                    {createDigits()}
                    <button onClick={() => {updateScreen(0)}}>0</button>
                    <button onClick={() => {updateScreen(".")}}>.</button>
                    <button onClick={evaluate}>=</button>
                </div>
            </div>
        </div>
    </div>
    );

}
export default App;
