import logo from './logo.svg';
import './App.css';
import {Planets} from "./Planets";
import {useState} from "react";

function App() {

    const [content, setContent] = useState([]);
    const [screen, setScreen] = useState("")
    //call this to keep displaying on screen.

    const updateContent = (number) => {
        content.push(number)
        console.log(content)
    }

    // equals
    const evaluate = () => {
        setScreen(eval(screen))
        // if eval throws an exception, display error and empty array.
    }
    //updates values of string
    const updateScreen = (number) => {
        // populates array
        updateContent(number)
        let temp = ""
        setScreen(screen + content[content.length - 1])
    }

    const deleteContent = () =>{
        content.pop()
        console.log(screen.slice(0, screen.length - 1))
        //setScreen(screen.slice(0, screen.length - 1))
        console.log(content)
    }
    return (
        <div className="App">Hello World
            <div id="everything">
                <div id="calculator">
                    <div id="screen">{screen}</div>
                        <div id="operations">
                            <button>/</button>
                            <button>x</button>
                            <button>-</button>
                            <button>+</button>
                            <button onClick={deleteContent}>DEL</button>
                        </div>
                <div id="numbers">
                    <button onClick={ () => {updateScreen(1)}}>1</button>
                    <button onClick={ () => {updateScreen(2)}}>2</button>
                    <button onClick={() => {updateScreen(3)}}>3</button>
                    <button onClick={() => {updateScreen(3)}}>4</button>
                    <button onClick={() => {updateScreen(3)}}>5</button>
                    <button onClick={() => {updateScreen(3)}}>6</button>
                    <button onClick={() => {updateScreen(3)}}>7</button>
                    <button onClick={() => {updateScreen(8)}}>8</button>
                    <button onClick={() => {updateScreen(9)}}>9</button>
                    <button onClick={() => {updateScreen(0)}}>0</button>
                    <button onClick={() => {updateScreen(".")}}>.</button>
                    <button onClick={evaluate}>=</button>
                </div>
            </div>
        </div>
    </div>
    );

}
export default App;
